/// <reference path="register.service.ts" />
/// <reference path="../login/login.service.ts" />
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { RegisterService } from '../register/register.service';
import { Users } from '../user/users';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  registerForm: FormGroup;
  constructor(
    private formBuilder: FormBuilder,
    private registerService: RegisterService,
    private router: Router
  ) {
    this.registerForm = this.formBuilder.group({
      nombre: ['', Validators.required],
      apellido: ['', Validators.required],
      doctoIdent: ['', Validators.required],
      email: ['', Validators.required],
      clave: ['', Validators.required],
      cia: [10, Validators.required]
    });
  }

  onSubmit() {
    const userData: Users = this.registerForm.value as Users;
    this.registerService.register(userData).subscribe((respuesta) => {
      console.log('Respuesta del registro:', respuesta);
      // Resto del código en el componente
      this.router.navigateByUrl('/Home');
    });
    //this.registerService.register(userData).subscribe({
    //  next: () => {
    //    this.router.navigateByUrl('/Home');
    //  },
    //  error: (error) => {
    //    console.error('Error during registration:', error);
    //  }
    //});
  }
}
<div class="d-flex justify-content-center mt-5">
    <div class="col-3 register-form">
        <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
            <div class="title mb-4">
                <h1 class="mb-3">
                    Registro de usuario
                </h1>
            </div>
            <div class="mb-3">
                <input type="text" formControlName="nombre" class="form-control" id="txtName" placeholder="Nombre">
            </div>
            <div class="mb-3">
                <input type="text" formControlName="apellido" class="form-control" id="txtLastname" placeholder="Apellido">
            </div>
            <div class="mb-3">
                <input type="number" formControlName="doctoIdent" class="form-control" id="txtNumberId" placeholder="Documento de identidad">
            </div>
            <div class="mb-3">
                <input type="email" formControlName="email" class="form-control" id="txtEmail" placeholder="Correo electrónico">
            </div>
            <div class="mb-3">
                <input type="password" formControlName="clave" class="form-control" id="txtclave" placeholder="Clave de usuario">
            </div>
            <div class="d-grid">
                <button class="btn btn-lg btn-primary mt-3" type="submit">
                    Registrar usuario
                </button>
            </div>
        </form>
    </div>
</div>
