import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginService } from './login.service';
import { Users } from '../user/users';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  title: string = 'Login From';

  loginForm = new FormGroup({
    email: new FormControl('', Validators.required),
    clave: new FormControl('', Validators.required)
  })
  constructor(private loginService: LoginService, private router: Router) { }

  onSubmit() {
    this.loginService.login(this.loginForm.value).subscribe({
      next: () => this.router.navigateByUrl('/home'),
      error: () => alert("Please check Credential...!")
    })
  }
}
<div class="d-flex justify-content-center mt-5">
    <div class="col-3">
        <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
            <div class="text-center mb-4">
                <h1 class="mb-3">
                    Iniciar sesión
                </h1>
            </div>
            <div class="form-floating mb-3">
                <input type="email" formControlName="email" class="form-control" id="txtEmail" placeholder="Correo electrónico">
            </div>
            <div class="form-floating">
                <input type="password" formControlName="clave" class="form-control" id="txtclave" placeholder="Clave de usuario">
            </div>
            <div class="d-grid">
                <button class="btn btn-lg btn-primary mt-3" type="submit">
                    Ingresar
                </button>
            </div>
        </form>
    </div>
</div>
