/// <reference path="register.service.ts" />
import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { RegisterService } from '../register/register.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  
  registerForm = new FormGroup({
    name: new FormControl('', Validators.required),
    lastname: new FormControl('', Validators.required),
    numberId: new FormControl('', Validators.required),
    email: new FormControl('', Validators.required),
    password: new FormControl('', Validators.required),
    cia: new FormControl('', Validators.required),
  })

  constructor(private registerService: RegisterService, private router: Router) { }

  onSubmit() {
    this.registerService.register(this.registerForm.value).subscribe(
      () => {
        // Registro exitoso, redirigir a la página de inicio
        this.router.navigateByUrl('/home');
      },
      (error) => {
        // Manejar errores específicos o mostrar un mensaje general
        if (error.status === 401) {
          alert("Credenciales incorrectas. Por favor, verifícalas.");
        } else {
          alert("Ocurrió un error durante el registro. Por favor, inténtalo de nuevo.");
        }
      }
  )
  }
}
<div class="d-flex justify-content-center mt-5">
    <div class="col-3 register-form">
        <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">
            <div class="title mb-4">
                <h1 class="mb-3">
                    Registro de usuario
                </h1>
            </div>
            <div class="mb-3">
                <input type="text" formControlName="name" class="form-control" id="floatingName" placeholder="Nombre">
            </div>
            <div class="mb-3">
                <input type="text" formControlName="lastname" class="form-control" id="floatingLastname" placeholder="Apellido">
            </div>
            <div class="mb-3">
                <input type="number" formControlName="numberId" class="form-control" id="floatingNumberId" placeholder="Documento de identidad">
            </div>
            <div class="mb-3">
                <input type="email" formControlName="email" class="form-control" id="floatingEmail" placeholder="Correo electrónico">
            </div>
            <div class="mb-3">
                <input type="password" formControlName="password" class="form-control" id="floatingPassword" placeholder="Clave de usuario">
            </div>
            <div class="mb-3" hidden>
                <input type="number" formControlName="cia" class="form-control" value="10" id="floatingCia">
            </div>
            <div class="d-grid">
                <button class="btn btn-lg btn-primary mt-3" type="submit">
                    Registrar usuario
                </button>
            </div>
        </form>
    </div>
</div>
